mapper.select:
select j.jobs_id, c.comp_name, c.photo, title, career, education, position, address, end_date
,ifnull(( select user_scrap_id from user_scrap_tb where user_id = #{userId} and jobs_id = j.jobs_id ),null) user_scrap_id
from jobs_tb j join comp_tb c on j.comp_id = c.comp_id where title regexp #{keyword} or c.comp_name regexp #{keyword} or career regexp #{keyword} or education regexp #{keyword} or position regexp #{keyword} or address regexp #{keyword}

select j.jobs_id, c.photo, c.comp_name, j.title, j.content, j.comp_id, j.career, j.education, j.position, j.address, j.receipt, c.homepage, c.representative_name, j.end_date
,ifnull(( select user_scrap_id from user_scrap_tb where user_id = #{userId} and jobs_id = j.jobs_id ),null) user_scrap_id
from comp_tb c join jobs_tb j on c.comp_id = j.comp_id where j.jobs_id = #{jobsId}
